import React from 'react'
import ReactDOM from 'react-dom'



class ProductCategoryRow extends React.Component{
    render(){
    const category = this.props.category;
     return(
      <tr>
        <th colSpan="2">
            {category}
        </th>
      </tr>
    );
 }
}

class ProductRow extends React.Component{
    render(){
        const product = this.props.product;
        const name = product.stocked ?
          product.name : 
           <span style={{color: 'black'}}>
            {product.name}
           </span>;

        return (
         <tr>
             <td>{name}</td>
             <td>{product.price}</td>
         </tr>
        );
    }
}

class ProductTable extends React.Component {
  render() {
   const filterText = this.props.filterText;
   const inStockOnly = this.props.inStockOnly;

   const rows = [];
   let lastCategory = null;

   this.props.products.forEach((product) => {
      if (product.name.indexOf(filterText) === -1){
       return;
     }
     if (inStockOnly && !product.stocked){
       return;
     }
     if (product.category !== lastCategory){
         rows.push(
          <ProductCategoryRow 
            category={product.category}
            key={product.category}/>
      );
     }
         rows.push(
          <ProductRow
            product={product}
            key={product.name}
             />
        );
    lastCategory = product.category;
    });

    return (
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    );
  }
}

class SearchBar extends React.Component{
        constructor(props){
            super(props);
            this.inStockChange = this.inStockChange.bind(this);
            this.filterTextChange = this.filterTextChange.bind(this);

        }
    filterTextChange(e){
        this.props.onFilterTextChange(e.target.value);
    }
    inStockChange(e){
        this.props.onStockChange(e.target.checked);
    }
    render() {        
        return(
            <form>
                <input type="text"
                    placeholder="Search..."
                    value={this.props.filterText}
                    onChange={this.filterTextChange}
                    />
                <p>
                    <input 
                        type="checkbox"
                        checked={this.props.inStockOnly}
                        onChange={this.inStockChange}
                        />
                    {' '}
                    Only show products in stock
                </p>
            </form>
        );
    }
}

class FilterProductTable extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            filterText: '',
            inStockOnly: false
        };
        this.filterTextChange = this.filterTextChange.bind(this);
        this.inStockChange = this.inStockChange.bind(this);
    }

    filterTextChange(filterText) {
        this.setState({filterText: filterText});
    }
    inStockChange(inStockOnly) {
        this.setState({inStockOnly: inStockOnly});
    }


    render() {
        return(
            <div>
             <SearchBar 
                    filterText={this.state.filterText}
                    inStockOnly={this.state.inStockOnly}
                    onFilterTextChange={this.filterTextChange}
                    onStockChange={this.inStockChange}
                    />
                <ProductTable 
                    products={this.props.products} 
                    filterText={this.state.filterText}
                    isStockOnly={this.state.isStockOnly}
                    />
            </div>
        );
    }
}

export default FilterProductTable;
